<?php

namespace Styleguide\Http\Controllers;

use Illuminate\View\View;
use App\Http\Controllers\Controller;
use Illuminate\Http\Request;

class HeroFullSVGOverlayController extends Controller
{
    /**
     * Display the full width hero view.
     */
    public function index(Request $request): View
    {
        // Set this controller in the allowed controllers list
        config([
            'base.hero_text_controllers' => ['HeroFullSVGOverlayController'],
            'base.hero_contained' => false,
            'base.hero_full_controllers' => ['HeroFullSVGOverlayController'],
        ]);

        // Set option
        $request->data['base']['hero'] = collect($request->data['base']['hero'])->map(function ($item) {
            $item['option'] = "SVG Overlay";
            $item['secondary_relative_url']  = "data:image/svg+xml;base64,";

            return $item;
        })->toArray();

        return view('styleguide-childpage', merge($request->data));
    }
}
